import "@babel/polyfill"; // hacky fix that makes the web verison work
import React, { useEffect } from "react";
import { StatusBar } from "react-native";
import { NavigationContainer } from "@react-navigation/native";
import { createStackNavigator } from "@react-navigation/stack";

// screens
import HomeScreen from "./screens/HomeScreen";
import SplashScreen from "./screens/SplashScreen";
import LoginScreen from "./screens/LoginScreen";

// aws
import Amplify from "aws-amplify";
import { Auth } from "aws-amplify";
import awsmobile from "./aws-exports";
Amplify.configure({ ...awsmobile, Analytics: { disabled: true } }); // Note: Disabling analytics was a hacky way of getting warning to disappear

// contexts
import AuthContext from "./contexts/AuthContext";

// Theming
const theme = {
  dark: false,
  colors: {
    primary: "#e0e1dd",
    background: "#ffffff",
    card: "#e0e1dd",
    text: "#a7a7a7",
    hintText: "#a8a8a8",
    inputText: "#ffffff",
    border: "transparent",
    notification: "#000000",
  },
};

// Setup Layout
const Stack = createStackNavigator();

export default function App() {
  const [state, dispatch] = React.useReducer(
    (prevState, action) => {
      switch (action.type) {
        case "RESTORE_TOKEN":
          return {
            ...prevState,
            userToken: action.token,
            isLoading: false,
          };
        case "LOGIN":
          return {
            ...prevState,
            isSignout: false,
            userToken: action.token,
          };
        case "LOGOUT":
          return {
            ...prevState,
            isSignout: true,
            userToken: null,
          };
      }
    },
    {
      isLoading: true,
      isSignout: false,
      userToken: null,
    }
  );

  // on app start
  useEffect(() => {
    const restoreToken = async () => {
      let userToken;
      try {
        userToken = null; //await AsyncStorage.getItem('auth-token');
      } catch (e) {
        // Restoring token failed
        alert(e);
      }
      dispatch({ type: "RESTORE_TOKEN", token: userToken });
    };

    restoreToken();
  }, []);

  const authContext = React.useMemo(
    () => ({
      login: async (username: string, password: string) => {
        try {
          const user = await Auth.signIn(username, password);
          dispatch({ type: "LOGIN", token: "dummy-auth-token" });
        } catch (error) {
          console.log("error signing in", error);
        }
      },
      logout: async () => {
        try {
          await Auth.signOut();
          dispatch({ type: "LOGOUT" });
        } catch (error) {
          console.log("error signing out: ", error);
        }
      },
      signUp: async (data) => {
        dispatch({ type: "LOGIN", token: "dummy-auth-token" });
      },
    }),
    []
  );

  if (state.isLoading) {
    return <SplashScreen />;
  } else {
    return (
      <AuthContext.Provider value={authContext}>
        <NavigationContainer theme={theme}>
          <StatusBar backgroundColor={theme.colors.background} />
          <Stack.Navigator>
            {state.isLoading ? (
              <Stack.Screen name="Splash" component={SplashScreen} />
            ) : state.userToken == null ? (
              <Stack.Screen
                name="Login"
                component={LoginScreen}
                options={{
                  animationTypeForReplace: state.isSignout ? "pop" : "push",
                  headerShown: false,
                }}
              />
            ) : (
              <Stack.Screen name="Home" component={HomeScreen} />
            )}
          </Stack.Navigator>
        </NavigationContainer>
      </AuthContext.Provider>
    );
  }
}
